const WORDS_PER_MINUTE = 200;

function toPlainText(value) {
  return String(value || '')
    .replace(/<[^>]+>/g, ' ')
    .trim();
}

function estimateReadTimeMinutes(text) {
  const plain = toPlainText(text);
  if (!plain) return 1;
  const words = plain.split(' ').length;
  return Math.max(1, Math.ceil(words / WORDS_PER_MINUTE));
}

export function getPostReadTimeMinutes(post) {
  if (!post) return 1;

  if (Number.isFinite(post.readTime) && post.readTime > 0) {
    return post.readTime;
  }

  const source = post.contentHtml || post.content || post.description || '';
  return estimateReadTimeMinutes(source);
}

function withReadTime(post) {
  if (!post) return post;
  const readTime = getPostReadTimeMinutes(post);
  if (post.readTime === readTime) {
    return post;
  }
  return { ...post, readTime };
}

function hasValue(value) {
  return typeof value === 'string' ? value.trim().length > 0 : !!value;
}

function normalizeString(value) {
  return String(value || '').trim();
}

export function validateBlogData(locale) {
  const posts = blogPosts[locale] || [];
  const slugCounts = posts.reduce((acc, post) => {
    const slug = normalizeString(post && post.slug);
    if (slug) {
      acc[slug] = (acc[slug] || 0) + 1;
    }
    return acc;
  }, {});

  const duplicateSlugs = Object.keys(slugCounts).filter((slug) => slugCounts[slug] > 1);
  const slugSet = new Set(Object.keys(slugCounts));

  const errors = [];
  const missingRelatedRefs = [];

  posts.forEach((post, index) => {
    const postSlug = normalizeString(post && post.slug);
    const postErrors = [];

    if (!hasValue(postSlug)) postErrors.push('missing_slug');
    if (!hasValue(post && post.title)) postErrors.push('missing_title');
    if (!hasValue(post && post.description)) postErrors.push('missing_description');
    if (!hasValue(post && post.date)) postErrors.push('missing_date');
    if (!hasValue(post && post.category)) postErrors.push('missing_category');
    if (!Array.isArray(post && post.keywords)) postErrors.push('keywords_not_array');

    if (post && Array.isArray(post.relatedPosts)) {
      const brokenRelated = post.relatedPosts
        .map((slug) => normalizeString(slug))
        .filter((slug) => slug && !slugSet.has(slug));
      if (brokenRelated.length > 0) {
        missingRelatedRefs.push({ slug: postSlug || '(unknown)', refs: brokenRelated });
      }
    } else {
      postErrors.push('relatedPosts_not_array');
    }

    if (postErrors.length > 0) {
      errors.push({ index, slug: postSlug || '(unknown)', errors: postErrors });
    }
  });

  return {
    locale,
    total: posts.length,
    invalid: errors.length,
    valid: posts.length - errors.length,
    duplicateSlugs,
    missingRelatedRefs,
    errors,
    isValid: errors.length === 0 && duplicateSlugs.length === 0 && missingRelatedRefs.length === 0,
  };
}

export function validateAllBlogData() {
  const locales = Object.keys(blogPosts);
  const results = locales.map((locale) => validateBlogData(locale));
  const invalidLocales = results.filter((item) => !item.isValid);

  return {
    totalLocales: results.length,
    validLocales: results.length - invalidLocales.length,
    invalidLocales,
    results,
    isValid: invalidLocales.length === 0,
  };
}

const CATEGORY_LABELS = {
  ru: {
    guides: 'Гайды',
    branding: 'Брендирование',
    holidays: 'Праздники',
    business: 'Бизнес',
  },
  uz: {
    guides: "Qo'llanmalar",
    branding: 'Brendlash',
    holidays: 'Bayramlar',
    business: 'Biznes',
  },
};

function normalizeCategoryBucket(post, locale) {
  const category = String((post && post.category) || '').toLowerCase();
  const keywords = Array.isArray(post && post.keywords)
    ? post.keywords.join(' ').toLowerCase()
    : '';
  const bag = category + ' ' + keywords + ' ' + String((post && post.title) || '').toLowerCase();

  if (/guide|гайд|чек|qollanma|qo'llanma|how\s?to/i.test(bag)) return 'guides';
  if (/navruz|новый\s?год|bayram|сезон|праздник|holiday/i.test(bag)) return 'holidays';
  if (/brand|бренд|grav|грав|laser|lazer|logo|логотип|cluster|pillar|мерч/i.test(bag)) return 'branding';
  return 'business';
}

function tokenize(value) {
  return String(value || '')
    .toLowerCase()
    .replace(/[^\p{L}\p{N}\s]/gu, ' ')
    .split(/\s+/)
    .filter((token) => token.length > 2);
}

function sharedCount(listA, listB) {
  if (!listA.length || !listB.length) return 0;
  const setB = new Set(listB);
  return listA.reduce((acc, item) => acc + (setB.has(item) ? 1 : 0), 0);
}

export function getCategorySummary(locale, limit = 4) {
  const labels = CATEGORY_LABELS[locale] || CATEGORY_LABELS.ru;
  const posts = getPostsByLocale(locale);
  const counts = posts.reduce((acc, post) => {
    const bucket = normalizeCategoryBucket(post, locale);
    acc[bucket] = (acc[bucket] || 0) + 1;
    return acc;
  }, {});

  return Object.keys(counts)
    .map((slug) => ({ slug, name: labels[slug] || slug, count: counts[slug] }))
    .sort((a, b) => b.count - a.count)
    .slice(0, limit);
}

export function getRelatedPostsWeighted(locale, currentSlug, limit = 5, seedSlugs = []) {
  const current = getPostBySlug(locale, currentSlug);
  if (!current) return [];

  const posts = getPostsByLocale(locale).filter((post) => post.slug !== currentSlug);
  const currentKeywords = Array.isArray(current.keywords) ? current.keywords.map((keyword) => String(keyword).toLowerCase()) : [];
  const currentTitleTokens = tokenize(current.title);
  const currentBucket = normalizeCategoryBucket(current, locale);

  const manualPriority = new Set([
    ...(Array.isArray(current.relatedPosts) ? current.relatedPosts : []),
    ...(Array.isArray(seedSlugs) ? seedSlugs : []),
  ]);

  return posts
    .map((candidate) => {
      const candidateKeywords = Array.isArray(candidate.keywords) ? candidate.keywords.map((keyword) => String(keyword).toLowerCase()) : [];
      const candidateBucket = normalizeCategoryBucket(candidate, locale);
      const titleOverlap = sharedCount(currentTitleTokens, tokenize(candidate.title));
      const keywordOverlap = sharedCount(currentKeywords, candidateKeywords);

      let score = 0;
      if (manualPriority.has(candidate.slug)) score += 100;
      if (candidateBucket === currentBucket) score += 20;
      score += keywordOverlap * 8;
      score += titleOverlap * 2;

      return { candidate, score };
    })
    .sort((left, right) => {
      if (right.score !== left.score) return right.score - left.score;
      return new Date(right.candidate.date) - new Date(left.candidate.date);
    })
    .slice(0, limit)
    .map((row) => row.candidate);
}

/**
 * Get all posts for a locale, sorted by date (newest first)
 */
export function getPostsByLocale(locale) {
  const posts = blogPosts[locale] || blogPosts.ru;
  return [...posts]
    .sort((a, b) => new Date(b.date) - new Date(a.date))
    .map(withReadTime);
}

/**
 * Get a single post by slug and locale
 */
export function getPostBySlug(locale, slug) {
  const posts = blogPosts[locale] || blogPosts.ru;
  const post = posts.find(p => p.slug === slug) || null;
  return withReadTime(post);
}

export function getAlternateSlug(slug) {
  return getMappedAlternateSlug('ru', slug) || getMappedAlternateSlug('uz', slug) || null;
}

/**
 * Get related posts for a given post
 */
export function getRelatedPosts(locale, currentSlug) {
  return getRelatedPostsWeighted(locale, currentSlug, 5);
}

/**
 * Get all slugs for sitemap generation
 */
export function getAllSlugs() {
  const ruSlugs = blogPosts.ru.map(p => ({ locale: 'ru', slug: p.slug }));
  const uzSlugs = blogPosts.uz.map(p => ({ locale: 'uz', slug: p.slug }));
  return [...ruSlugs, ...uzSlugs];
}
